msg.payload = JSON.stringify({
    pv: 10,
    sp: 20,
    kp: 2,
    ki: 1,
    kd: 0.5
});
return msg;

// Values in mm
var a = msg.payload[0]; // TopTank in mm
flow.set('ttm', a);

var b = msg.payload[1]; // UnderTank in mm
flow.set('utm', b);


// Conversion factor: 4.825 litres per mm
var upvalueInLitres = 5.002;
var downvalueInLitres = 4.825;

// Total tank capacity in litres (example values, replace with actual capacities)
var TopTankCapacity = 5102; // Maximum capacity for TopTank in litres
var UnderTankCapacity = 6080; // Maximum capacity for UnderTank in litres

// Calculate values
var TopTankLitres = a * upvalueInLitres;
flow.set('ttl', TopTankLitres);

var UnderTankLitres = b * downvalueInLitres;
flow.set('ugl', UnderTankLitres);

// Calculate percentages
var TopTankPercentage = (TopTankLitres / TopTankCapacity) * 100;
flow.set('ttp', TopTankPercentage);

var UnderTankPercentage = (UnderTankLitres / UnderTankCapacity) * 100;
flow.set('utp', UnderTankPercentage);

var TOP_PLUS_UG = TopTankLitres + UnderTankLitres;
flow.set('totalwater', TOP_PLUS_UG);

// Construct the payload

// msg.payload = {
//     UnderLitres: UnderTankLitres
// }

// return msg;

if (msg.payload.method === "modeSelection") {
    return { payload: msg.payload.params };  // Wrap the string in an object
}

if (msg.payload.method === "setState") {
    return { payload: msg.payload.params };  // Wrap the string in an object
}

if (msg.payload.method === "setmotor") {
    return { payload: msg.payload.params };  // Wrap the string in an object
}

// // Convert true/false to 1/0
var autoRunning = msg.payload [0]?1:0;
flow.set("auto_Running", autoRunning);

// // Format the data as JSON for ThingsBoard
msg.payload = {
     "auto_Running": autoRunning
 };

return msg;

// Get the current valve status from flow context
var currentStatus = flow.get("auto_Running");

// Get the previous stored status
var previousStatus = flow.get("previous_autoRunning");

// If previousStatus is null (first run), initialize it without sending a message
if (previousStatus === null) {
    flow.set("previous_autoRunning", currentStatus);
    return null; // Do not send a message on first run
}

// If status hasn't changed, return null (no message)
if (currentStatus === previousStatus) {
    return null;
}

// Update the stored status
flow.set("previous_autoRunning", currentStatus);

// Format the data as JSON for ThingsBoard
msg.payload = { "Auto_Running": currentStatus };

return msg;

msg.payload = {
    // Separate individual values for TopTank
    "TopTank_mm": flow.get('ttm'),
    "TopTank_litres": flow.get('ttl'),  // Keep as a number
    "TopTank_percentage": flow.get('ttp'),  // Keep as a number

    // Separate individual values for UnderTank
    "UnderTank_mm": flow.get('utm'),
    "UnderTank_litres": flow.get('ugl'),  // Keep as a number
    "UnderTank_percentage": flow.get('utp'),  // Keep as a number
    "TOP_PLUS_UG": flow.get('totalwater')
};

return msg;

// Cumulative flow MQTT se mil raha hai, isko flow context mein store karte hain
var cumulativeFlow = msg.payload;
flow.set("CumulFlow", cumulativeFlow);

msg.payload = {
    "cumulative_flow": cumulativeFlow
};

return msg;

// Motor status retrieve 
var motorStatus = msg.payload[0] ? 1 : 0;
var previousStatus = flow.get("previous_motorStatus");
var cumulativeFlow = flow.get("CumulFlow");

// Last ON timestamp
var lastOnTime = flow.get("motor_onTime");

// Retrieve previous start and stop flow values
var startFlow = flow.get("start");
var stopFlow = flow.get("stop");

// Motor status ko store 
flow.set("motor_status", motorStatus);

// Ensure cumulative flow is valid
if (cumulativeFlow === undefined || cumulativeFlow === null) {
    return null;
}

// Initialize previousStatus on first run
if (previousStatus === undefined || previousStatus === null) {
    flow.set("previous_motorStatus", motorStatus);
    if (motorStatus === 1) {
        flow.set("motor_onTime", Date.now());
    }
    msg.payload = { 
        "motor_status": motorStatus, 
        "motor": motorStatus === 1 ? "Pump ON" : "Pump OFF",
        "cumulative_flow": cumulativeFlow
    };
    node.warn(msg.payload);
}

// Agar status change nahi hua toh kuch mat bhejo
if (motorStatus === previousStatus) {
    return null;
}

// Update stored status
flow.set("previous_motorStatus", motorStatus);

// Jab motor ON ho (0  1)
if (motorStatus === 1) {
    flow.set("motor_onTime", Date.now());
    startFlow = cumulativeFlow;
    flow.set("start", startFlow);

    msg.payload = { 
        "motor_status": 1,
        "motor": "Pump ON",
        "cumulative_flow": cumulativeFlow,
        "TimeM":flow.get("time")
    };
    return msg;
}

// Jab motor OFF ho (1  0)
if (motorStatus === 0 && lastOnTime) {
    var elapsedTime = Math.floor((Date.now() - lastOnTime) / 1000); // Convert milliseconds to seconds

    var elapsedTimeMin = elapsedTime / 60; // minutes

    flow.set("motor_onTime", null);


    stopFlow = cumulativeFlow;
    flow.set("stop", stopFlow);

    // Format elapsed time properly
    var totalHours = Math.floor(elapsedTime / 3600);
    var remainingMinutes = Math.floor((elapsedTime % 3600) / 60);
    var remainingSeconds = elapsedTime % 60;
    // var formattedTime = `${totalHours}_hrs_${remainingMinutes}_minutes_${remainingSeconds}_sec`;
    var formattedTime = `${totalHours}_hrs_${remainingMinutes}_mins`;
    //var fullMinutes = Math.floor(elapsedTime / 60); // total minutes (no seconds)
    var fullMinutesM = flow.set("fullMinutesM",Math.floor(elapsedTime / 60)); // total minutes (no seconds)

    // Calculate total water pumped and round to 2 decimal places
    var totalWaterPumped = ((parseFloat(stopFlow) - parseFloat(startFlow)) * 1000).toFixed(2);

    // Reset start and stop values
    flow.set("start", null);
    flow.set("stop", null);

    var litersPerMinute = parseFloat(totalWaterPumped) / parseFloat(elapsedTimeMin)


    msg.payload = { 
        "motor_status": 0,
        "motor": "Pump OFF",
        "motor_on_duration": formattedTime,
        "cumulative_flow": cumulativeFlow,
        "start_flow": startFlow,
        "stop_flow": stopFlow,
        "totalWaterPumpedTop": totalWaterPumped,
        "LPM_motor": litersPerMinute.toFixed(2),
        "Pump_on_minutes": flow.get("fullMinutesM"),
        "TimeM":flow.get("time")
    };
    return msg;
}

return null;

// Convert true/false to 1/0
var valveStatus = msg.payload[0] ? 1 : 0;
flow.set("Valve_status", valveStatus);

// Get previous valve status
var previousValveStatus = flow.get("previous_ValveStatus");

// Get the current underground tank liters
var currentLitres = flow.get("utm");

// Retrieve stored values
var valveStart = flow.get("valveStart");
var valveStop = flow.get("valveStop");
var lastOnTime = flow.get("Valve_onTime");

var now =  new Date();
var time = now.toLocaleTimeString('en-GB');  
flow.set("time",time);

// Format data for ThingsBoard
msg.payload = {
    "Valve_status": valveStatus
};

// Initialize previous status on first run
if (previousValveStatus === undefined || previousValveStatus === null) {
    flow.set("previous_ValveStatus", valveStatus);
    if (valveStatus === 1) {
        flow.set("Valve_onTime", Date.now());  // Store start time
        flow.set("valveStart", currentLitres); // Store liters when turned ON
    }
    return msg;
}

// If valve status has not changed, do nothing
if (valveStatus === previousValveStatus) {
    return null;
}

// Update previous valve status
flow.set("previous_ValveStatus", valveStatus);

// When valve turns ON, store underground tank liters and start time
if (valveStatus === 1) {
    flow.set("Valve_onTime", Date.now());
    valveStart = currentLitres;
    flow.set("valveStart", valveStart);

    msg.payload = {
        "Valve_status": 1,
        "Valve": "Valve ON",
        "valveStart": valveStart,
        "TimeV":flow.get("time")
    };
    return msg;
}

// When valve turns OFF, store underground tank liters and calculate usage & duration
if (valveStatus === 0) {
    valveStop = currentLitres;
    flow.set("valveStop", valveStop);

    var totalWaterUsed = 0;
    if (!isNaN(valveStart) && !isNaN(valveStop)) {
        totalWaterUsed = parseFloat(valveStop) - parseFloat(valveStart);
    }

    // Calculate ON duration
    var elapsedTime = lastOnTime ? (Date.now() - lastOnTime) / 1000 : 0; // Convert ms to sec
    var totalHours = Math.floor(elapsedTime / 3600);
    var remainingMinutes = Math.floor((elapsedTime % 3600) / 60);
    var remainingSeconds = Math.floor(elapsedTime % 60); // Corrected seconds calculation
    // var formattedTime = `${totalHours}_hrs_${remainingMinutes}_minutes_${remainingSeconds}_sec`;
    var formattedTime = `${totalHours}_hrs_${remainingMinutes}_mins`;
    var fullMinutesV = flow.set("fullMinutesV",Math.floor(elapsedTime / 60)); // total minutes (no seconds)

    var totalLitres = totalWaterUsed * 4.825;
    flow.set('midcwater', totalLitres);

    // var LPM = (totalWaterUsed * 4.825) / (elapsedTime / 60)
    var lpm = elapsedTime > 0 ? (totalLitres / (elapsedTime / 60)) : 0;



    msg.payload = {
        "Valve_status": 0,
        "Valve": "Valve OFF",
        "Valve_on_duration": formattedTime, // Corrected time format
        "valveStart": valveStart,
        "valveStop": valveStop,
        "totalWaterPumpedUnder": totalLitres,
        "LPM_valve": parseFloat(lpm.toFixed(2)),
        "Valve_on_minutes": flow.get("fullMinutesV"),
        "TimeV":flow.get("time")
        // "time_valve": new Date().toLocaleTimeString('en-GB')
    };

    // Reset stored values
    flow.set("valveStart", null);
    flow.set("valveStop", null);
    flow.set("Valve_onTime", null);

    return msg;
}

return null;

// Define threshold values for 'ttp' and 'utp'
const ttpThreshold = 55;  // Example threshold for ttp
const utpThreshold = 75;  // Example threshold for utp

// Get current values from flow context
var autoRunning = flow.get("auto_Running");
var ttp = flow.get("ttp");
var utp = flow.get("utp");

// Get previous stored status
var previousAutoRunning = flow.get("previous_autoRunning") || 1; // Default to 1 (running)
var previousAlertSent = flow.get("previousAlertSent") || false; // Track if alert was already sent

// Check if auto_Running is 0 and either ttp OR utp is below its threshold
if (autoRunning === 0 && (ttp < ttpThreshold || utp < utpThreshold)) {
    if (!previousAlertSent) {
        // Send message only once
        flow.set("previousAlertSent", true); // Mark alert as sent
        msg.payload = JSON.stringify({  // Convert to string
            "ALERT": "Auto Running stopped! Check Overhead Tank Percentage & Underground Tank Percentage.", 
            "Auto_Running": autoRunning, 
