    } 
// } else {
//     // Reset alert flag when conditions are not met
//     flow.set("previousAlertSent", false);
// }

// // Update stored status
// flow.set("previous_autoRunning", autoRunning);

return null; // No message if conditions aren't met
}

var a = flow.get('midcwater');

if (a < 0) {
    msg.topic = "Alert: MIDC Water Pump Issue";
    msg.payload = "The MIDC water reading is negative (" + a + "). This may indicate that water is not being pumped. Please check the system.";
    return msg; // Sends the message to the email node
}

// If value is not negative, do nothing
return null;

// // Convert true/false to 1/0
var RelayStatus = msg.payload [0]?1:0;
flow.set("RelayStatus", RelayStatus);

// // Format the data as JSON for ThingsBoard
msg.payload = {
    "RelayStatus": RelayStatus
 };

return msg;

var a = flow.get('RelayStatus');

msg.payload = a;
return msg;

// Raw Values from Sensor
var a = msg.payload[0]; // TopTank 
flow.set('topTankRaw', a);

var b = msg.payload[1]; // UnderTank 
flow.set('ugTankRaw', b);

// Calculation i.e Scaling part
// for top tank
let t_input_min = 200;
let t_input_max = 1000;
let t_output_min = 0;
let t_output_max = 2000;
let t_scaled = ((a - t_input_min) * (t_output_max - t_output_min)) / 
             (t_input_max - t_input_min) + t_output_min;

//For Undergroung tank
let u_input_min = 200;
let u_input_max = 1000;
let u_output_min = 0;
let u_output_max = 2000;
let u_scaled = ((b - u_input_min) * (u_output_max - u_output_min)) / 
             (u_input_max - u_input_min) + u_output_min;

msg.payload = {
    toptankmm: t_scaled,
    ugtankmm: u_scaled
};
return msg;

// Convert boolean to 1 or 0
let value = msg.payload === true ? 1 : 0;

// Save to context with topic name
if (msg.topic === "bypassPumpTrip") {
    flow.set("bypassPumpTrip", value);
}
else if (msg.topic === "inputPumpTrip") {
    flow.set("inputPumpTrip", value);
}

// Optional: return all stored values for debug
msg.payload = {
    bypassPumpTrip: flow.get("bypassPumpTrip"),
    inputPumpTrip: flow.get("inputPumpTrip")
};

return msg;

//Alarm 
var bypumpT = flow.get("bypassPumpTrip");
var pumpT = flow.get("inputPumpTrip");
var alarmAck = flow.get("alarmAck"); 
let pumptrip = 0;
let critAlarm = 0;
if (bypumpT === 0 && pumpT === 0) {
    pumptrip = 1;
    flow.set("pumptrip", pumptrip);
}
if (pumptrip === 1) {
    critAlarm = 1;
    flow.set("critAlarm", critAlarm);
}
// Alarm acknowledge 
if (alarmAck === 1) {
    pumptrip = 0;
    flow.set("pumptrip", pumptrip);
}

//Auto Running
let autoRun = 0;
if (flow.get("critAlarm") === 0 && flow.get("auto_running") === 0) {
    autoRun = 1; 
    flow.set("autoRun", autoRun);
}
//Auto(valve and pump)
let valve = 0;
let pump = 0 ;
let auto_running = flow.get("auto_running");
if (auto_running === 1){
    if (auto_running === 1 && flow.get("UnderTank_percentage") < 90) {
        valve = 1;
        flow.set("valve", valve);
    } else if (flow.get("UnderTank_percentage") >= 98) {
        valve = 0;
        flow.set("valve", valve);
    }
    if (auto_running === 1 && flow.get("TopTank_percentage") < 55 && flow.get("UnderTank_percentage") > 30) {
        pump = 1;
        flow.set("autoRun", autoRun);
    } else if (flow.get("TopTank_percentage") >= 98 || flow.get("UnderTank_percentage") <= 30) {
        pump = 0;
        flow.set("autoRun", autoRun);
    }
}
//Manual
let valve_on = 0;
let pump_on = 0;
if (auto_running === 0){
    if (valve_on === 1 && flow.get("UnderTank_percentage") < 90) {
        valve = 1;
    } else if (flow.get("UnderTank_percentage") >= 98) {
        valve = 0;
    }
    if (pump_on === 0 && flow.get("TopTank_percentage") < 55 && flow.get("UnderTank_percentage") > 30) {
        pump = 1;
    } else if (flow.get("TopTank_percentage") >= 98 || flow.get("UnderTank_percentage") <= 30) {
        pump = 0;
    }
}

msg.payload = {
    bypumpT: bypumpT,
    pumpT: pumpT,
    pumptrip: pumptrip,
    critAlarm : critAlarm,
    valve : valve,
    pump : pump
};
return msg;

// ====== INPUTS ======
let bypumpT = flow.get("bypassPumpTrip"); //M513
let pumpT = flow.get("inputPumpTrip"); //X0
let alarmAck = flow.get("alarmAck"); //M4
let autoMode = flow.get("autoMode");  //M10
let modeSelection = flow.get("auto_Running"); //M512
let valve_on = flow.get("valve_on"); //Y0
let pump_on = flow.get("pump_on"); //Y1

let underTank = 98; //flow.get("UnderTank_percentage");
let topTank =90; //flow.get("TopTank_percentage");

let lastUnderLevel = flow.get("lastUnderLevel") || underTank;
let lastTopLevel = flow.get("lastTopLevel") || topTank;

let now = Date.now();
let date = new Date();
let hour = 12; //date.getHours();
let minute = date.getMinutes();

// ====== ALARM LOGIC ======
let pumptrip = flow.get("pumptrip") || 0;
let critAlarm = flow.get("critAlarm") || 0;

if (bypumpT === 0 && pumpT === 0) {
    pumptrip = 1;
}else {pumptrip =0
}
if (alarmAck === 1) {
    pumptrip = 0;
}
critAlarm = pumptrip;

flow.set("pumptrip", pumptrip);
flow.set("critAlarm", critAlarm);

// ====== AUTO MODE LATCH ======
//let auto_running = flow.get("auto_running") || 0;
if (modeSelection === 1 && critAlarm === 0) {
    autoMode = 1;
} else {
    autoMode = 0;
}
flow.set("autoMode", autoMode);

// ====== VALVE AND PUMP CONTROL ======
let valve = 0;
let pump = 0;

// ---- AUTO MODE ----
if (autoMode === 1) {
    // PUMP START CONDITION
    if (topTank <= 55 && underTank > 30) {
        let pumpStartTimer = flow.get("pumpStartTimer") || null;
        if (!pumpStartTimer) flow.set("pumpStartTimer", now);
        else if (now - pumpStartTimer >= 10000) pump = 1;
    } else {
        flow.set("pumpStartTimer", null);
    }

    if (topTank >= 90 || underTank <= 30) {
        pump = 0;
        flow.set("pumpStartTimer", null);
    }

    // VALVE CONDITION (Pump-driven or level-based)
    if (pump === 1) {
        valve = 1;
        flow.set("valve_from_pump", true);
    } else if (underTank < 90) {
        let valveTimer = flow.get("valveDelayTimer") || null;
        if (!valveTimer) flow.set("valveDelayTimer", now);
        else if (now - valveTimer >= 10000) valve = 1;
    } else {
        flow.set("valveDelayTimer", null);
    }

    if (underTank >= 98) valve = 0;
}

// ---- MANUAL MODE ----
if (autoMode === 0) {
    if (valve_on === 1 && underTank < 90){ valve = 1;}
    else if (underTank >= 98) {valve = 0;}

    if (pump_on === 1 && topTank < 55 && underTank > 30) pump = 1;
    else if (topTank >= 98 || underTank <= 30) pump = 0;
}

// ====== 2AM NIGHTTIME VALVE ROUTINE ======

let nightValveFlag = flow.get("nightValveFlag") || false;
if (hour === 2 && !nightValveFlag) {
    valve = 1;
    flow.set("nightValveFlag", true);
    flow.set("nightValveStart", now);
} else if (hour !== 2) {
    flow.set("nightValveFlag", false);
}

if (flow.get("nightValveStart")) {
    if (underTank >= 98) {
        valve = 0;
        flow.set("nightValveStart", null);
    } else if (now - flow.get("nightValveStart") >= 20 * 60 * 1000 && underTank <= lastUnderLevel) {
        flow.set("emailNight", "Valve ON since 2 AM but tank level not increasing.");
        valve = 0;
    }
}

// ====== MIDC WATER MISSING DETECTION ======
if (pump === 0 && valve === 1 && underTank <= lastUnderLevel) {
    flow.set("emailMIDC", "Pump OFF but valve ON and UnderTank level not increasing.");
}else {flow.set("emailMIDC", null);}

// ====== PUMP SAFETY SHUTDOWN ======
let pumpStartTime = flow.get("pumpStartTime") || null;
if (pump === 1 && !pumpStartTime) {
    flow.set("pumpStartTime", now);
} else if (pump === 1 && topTank <= lastTopLevel && now - pumpStartTime >= 2 * 60 * 1000) {
    pump = 0;
    flow.set("emailPump", "Pump turned OFF: TopTank not increasing. Safety shutdown triggered.");
    flow.set("pumpRetryTime", now + 15 * 60 * 1000);  // 15 min later
    flow.set("pumpRetryEnabled", true);
    flow.set("pumpStartTime", null);
}else{ flow.set("emailPump",null);}

if (flow.get("pumpRetryEnabled") && now >= flow.get("pumpRetryTime")) {
    if (topTank <= lastTopLevel) {
        pump = 0;
        flow.set("emailPumpFinal", "Pump permanently OFF: TopTank still not increasing after retry.");
        flow.set("pumpRetryEnabled", false);
    } else {
        pump = 1; // retry successful
        flow.set("pumpRetryEnabled", false);
        flow.set("emailPumpFinal", null);
    }
}

// ====== AUTO MODE RESET AFTER 30 MIN ======
let autoModeChangeTime = flow.get("autoModeChangeTime") || null;
if (autoMode === 0 && !autoModeChangeTime) {
    flow.set("autoModeChangeTime", now);
} else if (autoMode === 0 && now - autoModeChangeTime >= 30 * 60 * 1000) {
    flow.set("autoMode", 1);  // restore
    flow.set("autoModeChangeTime", null);
} else if (autoMode === 1) {
    flow.set("autoModeChangeTime", null);
}

// ====== UPDATE LAST LEVELS ======
flow.set("lastUnderLevel", underTank);
flow.set("lastTopLevel", topTank);

// ====== OUTPUT PAYLOAD ======
msg.payload = {
    bypumpT : bypumpT,
    pumpT : pumpT,
    pumptrip: pumptrip,
    critAlarm: critAlarm,
    modeSelection : modeSelection,
    autoMode: autoMode,
    valve_on : valve_on,
    pump_on :pump_on,
    nightValveFlag : nightValveFlag,
    valve: valve,
    pump: pump,
    emails: {
        night: flow.get("emailNight") || null,
        midc: flow.get("emailMIDC") || null,
        pump: flow.get("emailPump") || null,
        pumpFinal: flow.get("emailPumpFinal") || null
    }
};
return msg;

// ========== INPUTS ==========
let bypumpT = flow.get("bypassPumpTrip");
let pumpT = flow.get("inputPumpTrip");
