    flow.set("valveDelayTimer", null);
    flow.set("pumpStartTimer", null);
    flow.set("pump", pump);
    flow.set("valve", valve);
    return null;
}

// === PRESERVE RETRY STATE ===
let retryPump = flow.get("pumpRetryInProgress") || false;
let retryValve = flow.get("valveRetryInProgress") || false;

if (retryPump || retryValve) {
    pump = flow.get("pump") || 0;
    valve = flow.get("valve") || 0;
    flow.set("pump", pump);
    flow.set("valve", valve);
    return null;
}

// ---- AUTO MODE ----
