    if (!lockPump && topTank <= 55 && underTank > 30) {
        let pumpStartTimer = flow.get("pumpStartTimer") || null;
        if (!pumpStartTimer) flow.set("pumpStartTimer", now);
        else if (now - pumpStartTimer >= 10000) pump = 1;
    } else flow.set("pumpStartTimer", null);

    if (topTank >= 90 || underTank <= 30 || flow.get("shutdownPump") == true) {
        pump = 0;
        flow.set("pumpStartTimer", null);
        flow.set("shutdownPump", false);
    }

    if (!lockValve && pump === 1) {
        valve = 1;
        flow.set("valve_from_pump", true);
    } else if (!lockValve && underTank < 90) {
        let valveTimer = flow.get("valveDelayTimer") || null;
        if (!valveTimer) flow.set("valveDelayTimer", now);
        else if (now - valveTimer >= 10000) valve = 1;
    } else flow.set("valveDelayTimer", null);

    if (underTank >= 98 || flow.get("shutdownValve") == true) {
        valve = 0;
        flow.set("shutdownValve", false);
    }
}

// ---- MANUAL MODE ----
if (autoMode === 0) {
    if (!lockValve && valve_on === 1 && underTank < 90) valve = 1;
    else if (underTank >= 98 || flow.get("shutdownValve") == true) {
        valve = 0;
        flow.set("shutdownValve", false);
    }

    if (!lockPump && pump_on === 1 && topTank < 55 && underTank > 30) pump = 1;
    else if (topTank >= 98 || underTank <= 30 || flow.get("shutdownPump") == true) {
        pump = 0;
        flow.set("shutdownPump", false);
    }
}

// ========== STORE ==========
flow.set("pump", pump);
flow.set("valve", valve);
flow.set("lastUnderLevel", underTank);
flow.set("lastTopLevel", topTank);

