// Runtime helpers
function minutesSince(ts) {
    return ts ? Math.floor((now - ts) / 60000) : 0;
}

// History buffers for rolling check
let topHistory = flow.get("topTankHistory") || [];
topHistory.push({ ts: now, value: topTank });
topHistory = topHistory.filter(h => now - h.ts <= 10 * 60 * 1000); // keep 10 min
flow.set("topTankHistory", topHistory);

let underHistory = flow.get("underTankHistory") || [];
underHistory.push({ ts: now, value: underTank });
underHistory = underHistory.filter(h => now - h.ts <= 10 * 60 * 1000);
flow.set("underTankHistory", underHistory);

// Get reference levels from N minutes ago
function refLevel(history, minutesAgo, currentValue) {
    let ref = history.find(h => now - h.ts >= minutesAgo * 60 * 1000);
    return ref ? ref.value : currentValue;
}

let refTopLevel = refLevel(topHistory, 3, topTank);
flow.set("refTopLevel", refTopLevel);

let refUnderLevel = refLevel(underHistory, 5, underTank);
flow.set("refUnderLevel", refUnderLevel);

// Device states
let pump1 = flow.get("pump1") || 0;
let valve1 = flow.get("valve1") || 0;

// Flags
let nightPumpStarted = flow.get("nightPumpStarted") || false;
let nightValveStarted = flow.get("nightValveStarted") || false;

let pumpStartAt2am = flow.get("pumpStartAt2am") || false;
let pumpBlockedUntilNextDay = flow.get("pumpBlockedUntilNextDay") || false;
let valveBlockedUntilNextDay = flow.get("valveBlockedUntilNextDay") || false;

// Retry states
let pumpStartTime = flow.get("pumpStartTime") || null;
let pumpNoIncreaseStart = flow.get("pumpNoIncreaseStart") || null;
let pumpRetryScheduledAt = flow.get("pumpRetryScheduledAt") || null;
let pumpRetryStart = flow.get("pumpRetryStart") || null;

let valveStartTime = flow.get("valveStartTime") || null;
let valveNoIncreaseStart = flow.get("valveNoIncreaseStart") || null;
let valveRetryScheduledAt = flow.get("valveRetryScheduledAt") || null;
let valveRetryStart = flow.get("valveRetryStart") || null;


let emails = [];

// === DAILY RESET ===
// if(hour === 11 && minutes === 30){
if (hour === 2 && minutes === 0 && flow.get("lastResetDay") !== new Date().getDate()) {
    pumpBlockedUntilNextDay = false;
    valveBlockedUntilNextDay = false;
    nightPumpStarted = false;
    nightValveStarted = false;

    pumpRetryScheduledAt = null;
    pumpRetryStart = null;
    pumpNoIncreaseStart = null;

    valveRetryScheduledAt = null;
    valveRetryStart = null;
    valveNoIncreaseStart = null;

    flow.set("lastResetDay", new Date().getDate());
    emails.push({ topic: "Info", payload: " Daily reset at 2 AM: Flags and blocks cleared." });
}

// === PUMP START AT 2 AM ===
if (hour === 2 && minutes === 0 && topTank < 85 && !nightPumpStarted && !pumpBlockedUntilNextDay) {
    pump1 = 1;
    pumpStartTime = now;
    nightPumpStarted = true;
    pumpStartAt2am = true;

    flow.set("pump1", pump1);
    emails.push({ topic: "Info", payload: " Pump started at 2 AM (top tank < 85%)." });
}

// === PUMP STOP (TANK FULL) ===
if (pump1 === 1 && topTank >= 90) {
    pump1 = 0;
    pumpStartAt2am = false;
    nightPumpStarted = false;
    pumpStartTime = null;

    pumpRetryScheduledAt = null;
    pumpRetryStart = null;
    pumpNoIncreaseStart = null;

    flow.set("pump1", pump1);
    emails.push({ topic: "Info", payload: " Pump stopped (top tank >= 90%)." });

    // Start valve if needed
    if (underTank < 95 && !valveBlockedUntilNextDay) {
        valve1 = 1;
        valveStartTime = now;
        nightValveStarted = true;
        flow.set("valve1", valve1);
        emails.push({ topic: "Info", payload: " Valve started (underground tank < 95%)." });
    }
}

// === PUMP SAFETY: NO INCREASE ===
if (pump1 === 1 && Math.floor(topTank) <= Math.floor(refTopLevel)  && !pumpRetryStart) {
    if (!pumpNoIncreaseStart) pumpNoIncreaseStart = now;
    if (now - pumpNoIncreaseStart > 3 * 60 * 1000 && ((topTank - refTopLevel) < 2) ) {
        pump1 = 0;
        pumpStartTime = null;
        pumpNoIncreaseStart = null;
        pumpRetryScheduledAt = now + 60 * 60 * 1000; // 5 min retry
        flow.set("pump1", pump1);
        emails.push({ topic: "Alert", payload: " Pump stopped  no increase in top tank for 3 min. Retry in 5 min." });
    }
} else {
    pumpNoIncreaseStart = null;
}

// === PUMP RETRY START ===
if (pumpRetryScheduledAt && now >= pumpRetryScheduledAt && !pumpRetryStart && !pumpBlockedUntilNextDay && !(topTank >= 85)) {
    pump1 = 1;
    pumpRetryStart = now;
    pumpRetryScheduledAt = null;
    flow.set("pump1", pump1);
    emails.push({ topic: "Info", payload: " Pump retry started (3 min check)." });
}

// === PUMP RETRY CHECK ===
if (pumpRetryStart && (now - pumpRetryStart > 3 * 60 * 1000)) {
    if (Math.floor(topTank) <= Math.floor(refTopLevel) && ((topTank - refTopLevel) < 2) ) {
        pump1 = 0;
        pumpRetryStart = null;
        pumpBlockedUntilNextDay = true;
        nightPumpStarted = false;
        pumpStartAt2am = false;
        pumpStartTime = null;
        flow.set("pump1", pump1);
        emails.push({ topic: "Alert", payload: " Pump retry failed  Pump blocked until next day." });
    } else {
        pumpRetryStart = null;
        pumpNoIncreaseStart = null;
        pumpStartTime = now;
        emails.push({ topic: "Info", payload: " Pump retry successful  continuing normal run." });
    }
}

// === VALVE STOP (TANK FULL) ===
if (valve1 === 1 && underTank >= 95) {
    valve1 = 0;
    nightValveStarted = false;
    valveStartTime = null;

    valveRetryScheduledAt = null;
    valveRetryStart = null;
    valveNoIncreaseStart = null;

    flow.set("valve1", valve1);
    emails.push({ topic: "Info", payload: " Valve stopped (underground tank >= 98%)." });
}

// === VALVE SAFETY: NO INCREASE ===
if (valve1 === 1 && Math.floor(underTank) <= Math.floor(refUnderLevel) && ((underTank - refUnderLevel) < 2) && !valveRetryStart) {
    if (!valveNoIncreaseStart) valveNoIncreaseStart = now;
    if (now - valveNoIncreaseStart > 10 * 60 * 1000) {
        valve1 = 0;
        valveStartTime = null;
        valveNoIncreaseStart = null;
        valveRetryScheduledAt = now + 60 * 60 * 1000;
        flow.set("valve1", valve1);
        emails.push({ topic: "Alert", payload: " Valve stopped  no increase in underground tank for 5 min. Retry in 5 min." });
    }
} else {
    valveNoIncreaseStart = null;
}

// === VALVE RETRY START ===
if (valveRetryScheduledAt && now >= valveRetryScheduledAt && !valveRetryStart && !valveBlockedUntilNextDay) {
    valve1 = 1;
    valveRetryStart = now;
    valveRetryScheduledAt = null;
    flow.set("valve1", valve1);
    emails.push({ topic: "Info", payload: " Valve retry started (5 min check)." });
}

// === VALVE RETRY CHECK ===
