// Expecting msg.payload = { pump: 0/1, valve: 0/1 }
let pumpValue = Number(msg.payload.pump);

// Get last values from flow context
let lastPump = flow.get("lastPump") ?? null;


let changes = {};

// Pump change detection
if (lastPump !== pumpValue) {
    flow.set("lastPump", pumpValue);
    changes.pump = pumpValue;
}



// Only output if there is at least one change
if (Object.keys(changes).length > 0) {
    msg.payload = changes;
    return msg;
} else {
    return null;
}

